def largest_prod_seq(n):
    result = 0
    for i in range(0, len(n)-4):
        first = int(n[i])
        second = int(n[i+1])
        third = int(n[i+2])
        fourth = int(n[i+3])
        fifth = int(n[i+4])
        result_here =  first*second*third*fourth*fifth
        if result < result_here: 
            result = result_here 
    return result
    

def main():
    n = ''
    print(largest_prod_seq(n))
    print('Tests Passed!')
                   
if __name__ == '__main__':
    main()

